import React from 'react';

import MoviesList from './components/MoviesList';
import './App.css';
import { useState } from 'react';
import axios from 'react';

function App() {

  const [movies,setMovies]=useState([]);


  // const options = {
  //   method: 'GET',
  //   headers: {
  //     'X-RapidAPI-Key': 'SIGN-UP-FOR-KEY',
  //     'X-RapidAPI-Host': 'cricbuzz-cricket.p.rapidapi.com'
  //   }
  // };

  // fetch('https://cricbuzz-cricket.p.rapidapi.com/matches/v1/recent', options)
  //   .then(response => response.json())
  //   .then(response => console.log(response))
  //   .catch(err => console.error(err));
  const options = {
    method: 'GET',
    headers: {
      'X-RapidAPI-Key': 'SIGN-UP-FOR-KEY',
      'X-RapidAPI-Host': 'weatherapi-com.p.rapidapi.com'
    }
  };

  fetch('https://weatherapi-com.p.rapidapi.com/current.json?q=53.1%2C-0.13', options)
    .then(response => response.json())
    .then(response => console.log(response))
    .catch(err => console.error(err));





  const fetchMov=()=>{

    fetch('https://swapi.dev/api/films/').then((response)=>{
      return response.json()
    }).then((data)=>{

      const transformedData = data.results.map((movData)=>{
        return{
        title:movData.title,
        id:movData.episode_id,
        releaseDate:movData.release_date,
        openingText:movData.opening_crawl
    }} )
     console.log(transformedData);
     setMovies(transformedData);
    })

  }


  const dummyMovies = [
    {
      id: 1,
      title: 'Some Dummy Movie',
      openingText: 'This is the opening text of the movie',
      releaseDate: '2021-05-18',
    },
    {
      id: 2,
      title: 'Some Dummy Movie 2',
      openingText: 'This is the second opening text of the movie',
      releaseDate: '2021-05-19',
    },
  ];

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMov}>Fetch Movies</button>
      </section>
      <section>
        <MoviesList movies={movies} />
      </section>
    </React.Fragment>
  );
}

export default App;
